import React, { useState, useEffect } from 'react';
import { restaurantAPI } from '../utils/api';

const AdminRestaurants = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [formData, setFormData] = useState({ 
    name: '', 
    cuisine: '', 
    description: '', 
    image: '', 
    deliveryTime: '', 
    address: '', 
    phone: '' 
  });

  useEffect(() => {
    fetchRestaurants();
  }, []);

  const fetchRestaurants = async () => {
    try {
      const response = await restaurantAPI.getAll();
      setRestaurants(response.data);
    } catch (error) {
      console.error('Error fetching restaurants:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await restaurantAPI.update(editingId, formData);
      } else {
        await restaurantAPI.create(formData);
      }
      fetchRestaurants();
      setShowForm(false);
      setEditingId(null);
      setFormData({ name: '', cuisine: '', description: '' });
    } catch (error) {
      console.error('Error saving restaurant:', error);
    }
  };

  const handleEdit = (restaurant) => {
    setFormData({ 
      name: restaurant.name, 
      cuisine: restaurant.cuisine, 
      description: restaurant.description || '',
      image: restaurant.image || '',
      deliveryTime: restaurant.deliveryTime || '',
      address: restaurant.address || '',
      phone: restaurant.phone || ''
    });
    setEditingId(restaurant._id);
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this restaurant?')) {
      try {
        await restaurantAPI.delete(id);
        setRestaurants(restaurants.filter(r => r._id !== id));
      } catch (error) {
        console.error('Error deleting restaurant:', error);
      }
    }
  };

  if (loading) {
    return <div style={styles.loading}>Loading restaurants...</div>;
  }

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Manage Restaurants</h1>
        <button 
          style={styles.addBtn} 
          onClick={() => setShowForm(true)}
        >
          Add Restaurant
        </button>
      </div>

      {showForm && (
        <div style={styles.formContainer}>
          <form onSubmit={handleSubmit} style={styles.form}>
            <h3>{editingId ? 'Edit Restaurant' : 'Add Restaurant'}</h3>
            <input
              type="text"
              placeholder="Restaurant Name"
              value={formData.name}
              onChange={(e) => setFormData({...formData, name: e.target.value})}
              style={styles.input}
              required
            />
            <input
              type="text"
              placeholder="Cuisine Type"
              value={formData.cuisine}
              onChange={(e) => setFormData({...formData, cuisine: e.target.value})}
              style={styles.input}
              required
            />
            <input
              type="text"
              placeholder="Image URL"
              value={formData.image}
              onChange={(e) => setFormData({...formData, image: e.target.value})}
              style={styles.input}
              required
            />
            <input
              type="text"
              placeholder="Delivery Time (e.g., 30-45 min)"
              value={formData.deliveryTime}
              onChange={(e) => setFormData({...formData, deliveryTime: e.target.value})}
              style={styles.input}
              required
            />
            <input
              type="text"
              placeholder="Address"
              value={formData.address}
              onChange={(e) => setFormData({...formData, address: e.target.value})}
              style={styles.input}
              required
            />
            <input
              type="text"
              placeholder="Phone Number"
              value={formData.phone}
              onChange={(e) => setFormData({...formData, phone: e.target.value})}
              style={styles.input}
              required
            />
            <textarea
              placeholder="Description (optional)"
              value={formData.description}
              onChange={(e) => setFormData({...formData, description: e.target.value})}
              style={styles.textarea}
            />
            <div style={styles.formButtons}>
              <button type="submit" style={styles.saveBtn}>
                {editingId ? 'Update' : 'Add'}
              </button>
              <button 
                type="button" 
                onClick={() => {
                  setShowForm(false);
                  setEditingId(null);
                  setFormData({ name: '', cuisine: '', description: '' });
                }}
                style={styles.cancelBtn}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      <div style={styles.grid}>
        {restaurants.map(restaurant => (
          <div key={restaurant._id} style={styles.card}>
            <h3 style={styles.restaurantName}>{restaurant.name}</h3>
            <p style={styles.cuisine}>{restaurant.cuisine}</p>
            <p style={styles.description}>{restaurant.description}</p>
            <div style={styles.actions}>
              <button 
                style={styles.editBtn}
                onClick={() => handleEdit(restaurant)}
              >
                Edit
              </button>
              <button 
                style={styles.deleteBtn}
                onClick={() => handleDelete(restaurant._id)}
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '2rem',
    backgroundColor: '#fef7f0',
    minHeight: '90vh'
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '2rem'
  },
  title: {
    fontSize: '2rem',
    fontWeight: '700',
    color: '#333'
  },
  addBtn: {
    backgroundColor: '#ff6b35',
    color: 'white',
    padding: '0.75rem 1.5rem',
    border: 'none',
    borderRadius: '25px',
    fontSize: '1rem',
    fontWeight: '600',
    cursor: 'pointer'
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
    gap: '1.5rem'
  },
  card: {
    backgroundColor: 'white',
    padding: '1.5rem',
    borderRadius: '15px',
    boxShadow: '0 4px 15px rgba(0,0,0,0.1)'
  },
  restaurantName: {
    fontSize: '1.3rem',
    fontWeight: '600',
    color: '#333',
    marginBottom: '0.5rem'
  },
  cuisine: {
    fontSize: '1rem',
    color: '#ff6b35',
    fontWeight: '500',
    marginBottom: '0.5rem'
  },
  description: {
    fontSize: '0.9rem',
    color: '#666',
    marginBottom: '1rem'
  },
  actions: {
    display: 'flex',
    gap: '0.5rem'
  },
  editBtn: {
    backgroundColor: '#28a745',
    color: 'white',
    padding: '0.5rem 1rem',
    border: 'none',
    borderRadius: '20px',
    fontSize: '0.9rem',
    cursor: 'pointer'
  },
  deleteBtn: {
    backgroundColor: '#dc3545',
    color: 'white',
    padding: '0.5rem 1rem',
    border: 'none',
    borderRadius: '20px',
    fontSize: '0.9rem',
    cursor: 'pointer'
  },
  loading: {
    textAlign: 'center',
    padding: '4rem',
    fontSize: '1.2rem',
    color: '#666'
  },
  formContainer: {
    backgroundColor: 'white',
    padding: '2rem',
    borderRadius: '15px',
    boxShadow: '0 4px 15px rgba(0,0,0,0.1)',
    marginBottom: '2rem'
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '1rem'
  },
  input: {
    padding: '0.75rem',
    border: '1px solid #ddd',
    borderRadius: '8px',
    fontSize: '1rem'
  },
  textarea: {
    padding: '0.75rem',
    border: '1px solid #ddd',
    borderRadius: '8px',
    fontSize: '1rem',
    minHeight: '80px',
    resize: 'vertical'
  },
  formButtons: {
    display: 'flex',
    gap: '1rem'
  },
  saveBtn: {
    backgroundColor: '#28a745',
    color: 'white',
    padding: '0.75rem 1.5rem',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer'
  },
  cancelBtn: {
    backgroundColor: '#6c757d',
    color: 'white',
    padding: '0.75rem 1.5rem',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer'
  }
};

export default AdminRestaurants;
