import React, { useState, useEffect } from 'react';
import { restaurantAPI } from '../utils/api';
import { Link, useNavigate } from 'react-router-dom';

const AdminRestaurants = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({ 
    name: '', 
    cuisine: '', 
    description: '', 
    image: '', 
    deliveryTime: '', 
    address: '', 
    phone: '' 
  });
  const navigate = useNavigate();

  useEffect(() => {
    fetchRestaurants();
  }, []);

  const fetchRestaurants = async () => {
    try {
      const response = await restaurantAPI.getAll();
      setRestaurants(response.data);
    } catch (error) {
      console.error('Error fetching restaurants:', error);
      setError('Failed to fetch restaurants');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setMessage('');
    try {
      if (editingId) {
        await restaurantAPI.update(editingId, formData);
        setMessage('Restaurant updated successfully!');
      } else {
        await restaurantAPI.create(formData);
        setMessage('Restaurant added successfully!');
      }
      fetchRestaurants();
      setShowForm(false);
      setEditingId(null);
      setFormData({ name: '', cuisine: '', description: '', image: '', deliveryTime: '', address: '', phone: '' });
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      console.error('Error saving restaurant:', error);
      setError(error.response?.data?.message || 'Error saving restaurant');
    }
  };

  const handleEdit = (restaurant) => {
    setFormData({ 
      name: restaurant.name, 
      cuisine: restaurant.cuisine, 
      description: restaurant.description || '',
      image: restaurant.image || '',
      deliveryTime: restaurant.deliveryTime || '',
      address: restaurant.address || '',
      phone: restaurant.phone || ''
    });
    setEditingId(restaurant._id);
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this restaurant?')) {
      setError('');
      setMessage('');
      try {
        await restaurantAPI.delete(id);
        setRestaurants(restaurants.filter(r => r._id !== id));
        setMessage('Restaurant deleted successfully!');
        setTimeout(() => setMessage(''), 3000);
      } catch (error) {
        console.error('Error deleting restaurant:', error);
        setError(error.response?.data?.message || 'Error deleting restaurant');
      }
    }
  };

  if (loading) {
    return (
      <div style={styles.loadingContainer}>
        <div style={styles.spinner}></div>
        <p style={styles.loadingText}>Loading Restaurants...</p>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <div style={styles.sidebarHeader}>
          <div style={styles.logo}>
            <span style={styles.logoIcon}>ğŸ”</span>
            <span style={styles.logoText}>FoodFleet</span>
          </div>
          <div style={styles.adminBadge}>Admin Panel</div>
        </div>

        <nav style={styles.sidebarNav}>
          <div style={styles.navSection}>
            <h4 style={styles.navSectionTitle}>MAIN</h4>
            <Link to="/admin" style={styles.navItem}>
              <span style={styles.navIcon}>ğŸ“Š</span>
              Dashboard
            </Link>
          </div>

          <div style={styles.navSection}>
            <h4 style={styles.navSectionTitle}>MANAGEMENT</h4>
            <Link to="/admin/restaurants" style={styles.navItemActive}>
              <span style={styles.navIcon}>ğŸª</span>
              Restaurants
            </Link>
            <Link to="/admin/orders" style={styles.navItem}>
              <span style={styles.navIcon}>ğŸ“‹</span>
              Orders
            </Link>
            <Link to="/admin/users" style={styles.navItem}>
              <span style={styles.navIcon}>ğŸ‘¥</span>
              Users
            </Link>
          </div>
        </nav>

        <div style={styles.sidebarFooter}>
          <div style={styles.liveStatus}>
            <span style={styles.liveDot}></span>
            <span style={styles.liveText}>Live Updates</span>
          </div>
          <div style={styles.footerActions}>
            <Link to="/admin" style={styles.footerButton}>
              ğŸ  Dashboard
            </Link>
            <button onClick={() => {
              localStorage.removeItem('token');
              localStorage.removeItem('user');
              window.location.href = '/';
            }} style={styles.logoutButton}>
              ğŸšª Logout
            </button>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div style={styles.mainContent}>
        {/* Header */}
        <div style={styles.header}>
          <div style={styles.headerLeft}>
            <button onClick={() => navigate('/admin')} style={styles.backButton}>
              â† Back to Dashboard
            </button>
            <div>
              <h1 style={styles.pageTitle}>Restaurant Management</h1>
              <p style={styles.pageSubtitle}>Manage your restaurant partners</p>
            </div>
          </div>
          <button 
            onClick={() => setShowForm(true)}
            style={styles.addButton}
