            + Add Restaurant
          </button>
        </div>

        {/* Messages */}
        {message && <div style={styles.successMessage}>{message}</div>}
        {error && <div style={styles.errorMessage}>{error}</div>}

        {/* Form Modal */}
        {showForm && (
          <div style={styles.modal}>
            <div style={styles.modalContent}>
              <div style={styles.modalHeader}>
                <h3 style={styles.modalTitle}>
                  {editingId ? 'Edit Restaurant' : 'Add New Restaurant'}
                </h3>
                <button 
                  onClick={() => {
                    setShowForm(false);
                    setEditingId(null);
                    setFormData({ name: '', cuisine: '', description: '', image: '', deliveryTime: '', address: '', phone: '' });
                  }}
                  style={styles.closeButton}
                >
                  Ã—
                </button>
              </div>

              <form onSubmit={handleSubmit} style={styles.form}>
                <div style={styles.formGrid}>
                  <input
                    type="text"
                    placeholder="Restaurant Name"
                    value={formData.name}
                    onChange={(e) => setFormData({...formData, name: e.target.value})}
                    style={styles.input}
                    required
                  />
                  <input
                    type="text"
                    placeholder="Cuisine Type"
                    value={formData.cuisine}
                    onChange={(e) => setFormData({...formData, cuisine: e.target.value})}
                    style={styles.input}
                    required
                  />
                  <input
                    type="text"
                    placeholder="Image URL"
                    value={formData.image}
                    onChange={(e) => setFormData({...formData, image: e.target.value})}
                    style={styles.input}
                    required
                  />
                  <input
                    type="text"
                    placeholder="Delivery Time (e.g., 30-45 min)"
                    value={formData.deliveryTime}
                    onChange={(e) => setFormData({...formData, deliveryTime: e.target.value})}
                    style={styles.input}
                    required
                  />
                  <input
                    type="text"
                    placeholder="Address"
                    value={formData.address}
                    onChange={(e) => setFormData({...formData, address: e.target.value})}
                    style={styles.input}
                    required
                  />
                  <input
                    type="text"
                    placeholder="Phone Number"
                    value={formData.phone}
                    onChange={(e) => setFormData({...formData, phone: e.target.value})}
                    style={styles.input}
                    required
                  />
                </div>
                <textarea
                  placeholder="Description (optional)"
                  value={formData.description}
                  onChange={(e) => setFormData({...formData, description: e.target.value})}
                  style={styles.textarea}
                />
                <div style={styles.formButtons}>
                  <button type="submit" style={styles.saveButton}>
                    {editingId ? 'Update Restaurant' : 'Add Restaurant'}
                  </button>
                  <button 
                    type="button" 
                    onClick={() => {
                      setShowForm(false);
                      setEditingId(null);
                      setFormData({ name: '', cuisine: '', description: '', image: '', deliveryTime: '', address: '', phone: '' });
                    }}
                    style={styles.cancelButton}
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Restaurants Grid */}
        <div style={styles.restaurantsGrid}>
          {restaurants.map(restaurant => (
            <div key={restaurant._id} style={styles.restaurantCard}>
              <div style={styles.restaurantImage}>
                <img 
                  src={restaurant.image} 
                  alt={restaurant.name}
                  style={styles.image}
                  onError={(e) => {
                    e.target.src = 'https://via.placeholder.com/300x200?text=Restaurant';
                  }}
                />
              </div>
              <div style={styles.restaurantInfo}>
                <h3 style={styles.restaurantName}>{restaurant.name}</h3>
                <div style={styles.restaurantMeta}>
                  <span style={styles.cuisine}>{restaurant.cuisine}</span>
                  <span style={styles.deliveryTime}>ğŸ•’ {restaurant.deliveryTime}</span>
                </div>
                <p style={styles.address}>ğŸ“ {restaurant.address}</p>
                <p style={styles.phone}>ğŸ“ {restaurant.phone}</p>
                <div style={styles.actions}>
                  <button 
                    onClick={() => handleEdit(restaurant)}
                    style={styles.editButton}
                  >
                    âœï¸ Edit
                  </button>
                  <button 
                    onClick={() => handleDelete(restaurant._id)}
                    style={styles.deleteButton}
                  >
                    ğŸ—‘ï¸ Delete
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {restaurants.length === 0 && (
          <div style={styles.emptyState}>
            <span style={styles.emptyIcon}>ğŸª</span>
            <h3>No Restaurants Found</h3>
            <p>Add your first restaurant to get started</p>
            <button onClick={() => setShowForm(true)} style={styles.emptyButton}>
              Add Restaurant
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

