import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { restaurantAPI } from '../utils/api';

const RestaurantCard = ({ restaurant, onUpdate, onDelete }) => {
  const { user } = useAuth();
  const [showEditForm, setShowEditForm] = useState(false);
  const [editData, setEditData] = useState({
    name: restaurant.name,
    cuisine: restaurant.cuisine,
    address: restaurant.address,
    phone: restaurant.phone || '',
    deliveryTime: restaurant.deliveryTime,
    image: restaurant.image
  });

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await restaurantAPI.update(restaurant._id, editData);
      setShowEditForm(false);
      if (onUpdate) onUpdate();
    } catch (error) {
      console.error('Error updating restaurant:', error);
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this restaurant?')) {
      try {
        await restaurantAPI.delete(restaurant._id);
        if (onDelete) onDelete();
      } catch (error) {
        console.error('Error deleting restaurant:', error);
      }
    }
  };
  return (
    <div style={styles.card}>
      <img 
        src={restaurant.image} 
        alt={restaurant.name}
        style={styles.image}
        onError={(e) => {
          e.target.src = 'https://via.placeholder.com/300x200?text=Restaurant';
        }}
      />
      <div style={styles.content}>
        {showEditForm ? (
          <form onSubmit={handleUpdate} style={styles.editForm}>
            <input
              type="text"
              value={editData.name}
              onChange={(e) => setEditData({...editData, name: e.target.value})}
              style={styles.input}
              placeholder="Restaurant name"
              required
            />
            <input
              type="text"
              value={editData.cuisine}
              onChange={(e) => setEditData({...editData, cuisine: e.target.value})}
              style={styles.input}
              placeholder="Cuisine type"
              required
            />
            <input
              type="text"
              value={editData.address}
              onChange={(e) => setEditData({...editData, address: e.target.value})}
              style={styles.input}
              placeholder="Address"
              required
            />
            <input
              type="text"
              value={editData.phone}
              onChange={(e) => setEditData({...editData, phone: e.target.value})}
              style={styles.input}
              placeholder="Phone number"
            />
            <input
              type="text"
              value={editData.deliveryTime}
              onChange={(e) => setEditData({...editData, deliveryTime: e.target.value})}
              style={styles.input}
              placeholder="Delivery time"
              required
            />
            <input
              type="url"
              value={editData.image}
              onChange={(e) => setEditData({...editData, image: e.target.value})}
              style={styles.input}
              placeholder="Image URL"
            />
            <div style={styles.formButtons}>
              <button type="submit" style={styles.saveBtn}>Save</button>
              <button type="button" onClick={() => setShowEditForm(false)} style={styles.cancelBtn}>
                Cancel
              </button>
            </div>
          </form>
        ) : (
          <>
            <h3 style={styles.name}>{restaurant.name}</h3>
            <p style={styles.cuisine}>{restaurant.cuisine}</p>
            <div style={styles.info}>
              <span style={styles.rating}>‚≠ê {restaurant.rating}</span>
              <span style={styles.deliveryTime}>üïí {restaurant.deliveryTime}</span>
            </div>
            <p style={styles.address}>üìç {restaurant.address}</p>

            <div style={styles.buttonContainer}>
              <Link to={`/restaurant/${restaurant._id}`} style={styles.button}>
                View Menu
              </Link>

              {user?.isAdmin && (
                <div style={styles.adminButtons}>
                  <button onClick={() => setShowEditForm(true)} style={styles.editBtn}>
                    ‚úèÔ∏è Edit
                  </button>
                  <button onClick={handleDelete} style={styles.deleteBtn}>
                    üóëÔ∏è Delete
                  </button>
                </div>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

const styles = {
  card: {
    backgroundColor: 'white',
    borderRadius: '8px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    overflow: 'hidden',
    transition: 'transform 0.3s',
    cursor: 'pointer'
  },
  image: {
    width: '100%',
    height: '200px',
    objectFit: 'cover'
  },
  content: {
    padding: '1rem'
  },
  name: {
    margin: '0 0 0.5rem 0',
    fontSize: '1.25rem',
    fontWeight: 'bold'
  },
  cuisine: {
    margin: '0 0 0.5rem 0',
    color: '#666',
    fontSize: '0.9rem'
  },
  info: {
    display: 'flex',
    justifyContent: 'space-between',
    margin: '0.5rem 0',
    fontSize: '0.9rem'
  },
  rating: {
    color: '#ff6b35'
  },
  deliveryTime: {
    color: '#666'
  },
  address: {
    margin: '0.5rem 0',
    fontSize: '0.8rem',
    color: '#666'
  },
  buttonContainer: {
    marginTop: '1rem'
  },
  button: {
    display: 'inline-block',
    backgroundColor: '#ff6b35',
    color: 'white',
    padding: '0.5rem 1rem',
    textDecoration: 'none',
    borderRadius: '4px',
    fontSize: '0.9rem',
    width: '100%',
    textAlign: 'center',
    marginBottom: '0.5rem'
  },
  adminButtons: {
    display: 'flex',
    gap: '0.5rem'
  },
  editBtn: {
    backgroundColor: '#17a2b8',
    color: 'white',
    border: 'none',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '0.8rem',
    flex: 1
  },
  deleteBtn: {
    backgroundColor: '#dc3545',
    color: 'white',
    border: 'none',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '0.8rem',
    flex: 1
  },
  editForm: {
    display: 'flex',
    flexDirection: 'column',
    gap: '0.75rem'
  },
  input: {
    padding: '0.5rem',
    border: '1px solid #ddd',
    borderRadius: '4px',
    fontSize: '0.9rem'
  },
  formButtons: {
    display: 'flex',
    gap: '0.5rem'
  },
  saveBtn: {
    backgroundColor: '#28a745',
    color: 'white',
    border: 'none',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '0.9rem',
    flex: 1
  },
  cancelBtn: {
    backgroundColor: '#6c757d',
    color: 'white',
    border: 'none',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '0.9rem',
    flex: 1
  }
};

export default RestaurantCard;
