import React from 'react';

const HowItWorks = () => {
  return (
    <div style={styles.container}>
      <div style={styles.hero}>
        <div style={styles.content}>
          <h1 style={styles.title}>How It Works</h1>
          <p style={styles.subtitle}>
            Simple steps to get your favorite food delivered fast
          </p>
        </div>
      </div>

      <div style={styles.section}>
        <div style={styles.content}>
          <div style={styles.stepsGrid}>
            <div style={styles.step}>
              <div style={styles.stepIcon}>üîç</div>
              <h3 style={styles.stepTitle}>Browse & Select</h3>
              <p style={styles.stepText}>
                Discover restaurants and browse menus from your favorite cuisines
              </p>
            </div>

            <div style={styles.step}>
              <div style={styles.stepIcon}>üõí</div>
              <h3 style={styles.stepTitle}>Add to Cart</h3>
              <p style={styles.stepText}>
                Choose your items and customize your order exactly how you like it
              </p>
            </div>

            <div style={styles.step}>
              <div style={styles.stepIcon}>üí≥</div>
              <h3 style={styles.stepTitle}>Secure Payment</h3>
              <p style={styles.stepText}>
                Pay securely with multiple payment options available
              </p>
            </div>

            <div style={styles.step}>
              <div style={styles.stepIcon}>üöö</div>
              <h3 style={styles.stepTitle}>Fast Delivery</h3>
              <p style={styles.stepText}>
                Track your order in real-time and enjoy lightning-fast delivery
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    minHeight: '90vh',
    backgroundColor: '#fef7f0'
  },
  hero: {
    padding: '4rem 0',
    textAlign: 'center'
  },
  content: {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '0 2rem'
  },
  title: {
    fontSize: '3rem',
    fontWeight: '700',
    color: '#333',
    marginBottom: '1rem'
  },
  subtitle: {
    fontSize: '1.2rem',
    color: '#666',
    maxWidth: '600px',
    margin: '0 auto'
  },
  section: {
    padding: '4rem 0'
  },
  stepsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
    gap: '2rem'
  },
  step: {
    textAlign: 'center',
    padding: '2rem',
    backgroundColor: 'white',
    borderRadius: '20px',
    boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
    transition: 'transform 0.3s'
  },
  stepIcon: {
    fontSize: '3rem',
    marginBottom: '1rem'
  },
  stepTitle: {
    fontSize: '1.3rem',
    fontWeight: '600',
    color: '#333',
    marginBottom: '1rem'
  },
  stepText: {
    fontSize: '1rem',
    color: '#666',
    lineHeight: '1.6'
  }
};

export default HowItWorks;
