import React, { useState, useEffect } from 'react';
import { restaurantAPI } from '../utils/api';

const AdminRestaurants = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchRestaurants();
  }, []);

  const fetchRestaurants = async () => {
    try {
      const response = await restaurantAPI.getAll();
      setRestaurants(response.data);
    } catch (error) {
      console.error('Error fetching restaurants:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div style={styles.loading}>Loading restaurants...</div>;
  }

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Manage Restaurants</h1>
        <button style={styles.addBtn}>Add Restaurant</button>
      </div>

      <div style={styles.grid}>
        {restaurants.map(restaurant => (
          <div key={restaurant._id} style={styles.card}>
            <h3 style={styles.restaurantName}>{restaurant.name}</h3>
            <p style={styles.cuisine}>{restaurant.cuisine}</p>
            <p style={styles.description}>{restaurant.description}</p>
            <div style={styles.actions}>
              <button style={styles.editBtn}>Edit</button>
              <button style={styles.deleteBtn}>Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '2rem',
    backgroundColor: '#fef7f0',
    minHeight: '90vh'
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '2rem'
  },
  title: {
    fontSize: '2rem',
    fontWeight: '700',
    color: '#333'
  },
  addBtn: {
    backgroundColor: '#ff6b35',
    color: 'white',
    padding: '0.75rem 1.5rem',
    border: 'none',
    borderRadius: '25px',
    fontSize: '1rem',
    fontWeight: '600',
    cursor: 'pointer'
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
    gap: '1.5rem'
  },
  card: {
    backgroundColor: 'white',
    padding: '1.5rem',
    borderRadius: '15px',
    boxShadow: '0 4px 15px rgba(0,0,0,0.1)'
  },
  restaurantName: {
    fontSize: '1.3rem',
    fontWeight: '600',
    color: '#333',
    marginBottom: '0.5rem'
  },
  cuisine: {
    fontSize: '1rem',
    color: '#ff6b35',
    fontWeight: '500',
    marginBottom: '0.5rem'
  },
  description: {
    fontSize: '0.9rem',
    color: '#666',
    marginBottom: '1rem'
  },
  actions: {
    display: 'flex',
    gap: '0.5rem'
  },
  editBtn: {
    backgroundColor: '#28a745',
    color: 'white',
    padding: '0.5rem 1rem',
    border: 'none',
    borderRadius: '20px',
    fontSize: '0.9rem',
    cursor: 'pointer'
  },
  deleteBtn: {
    backgroundColor: '#dc3545',
    color: 'white',
    padding: '0.5rem 1rem',
    border: 'none',
    borderRadius: '20px',
    fontSize: '0.9rem',
    cursor: 'pointer'
  },
  loading: {
    textAlign: 'center',
    padding: '4rem',
    fontSize: '1.2rem',
    color: '#666'
  }
};

export default AdminRestaurants;
