import React, { useState, useEffect } from 'react';
import { restaurantAPI, orderAPI, menuAPI, userAPI } from '../utils/api';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalOrders: 0,
    totalRevenue: 0,
    activeUsers: 0,
    totalRestaurants: 0,
    pendingOrders: 0,
    todayOrders: 0
  });
  const [recentOrders, setRecentOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    fetchDashboardData();
    const interval = setInterval(() => {
      fetchDashboardData();
      setCurrentTime(new Date());
    }, 30000); // Update every 30 seconds

    const timeInterval = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    return () => {
      clearInterval(interval);
      clearInterval(timeInterval);
    };
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [ordersRes, usersRes, restaurantsRes] = await Promise.all([
        orderAPI.getAllOrders(),
        userAPI.getAll(),
        restaurantAPI.getAll()
      ]);

      const orders = [...ordersRes.data, ...JSON.parse(localStorage.getItem('userOrders') || '[]')];
      const users = usersRes.data;
      const restaurants = restaurantsRes.data;

      const today = new Date().toDateString();
      const todayOrders = orders.filter(order => 
        new Date(order.createdAt).toDateString() === today
      );

      setStats({
        totalOrders: orders.length,
        totalRevenue: orders.reduce((sum, order) => sum + (order.totalAmount || 0), 0),
        activeUsers: users.filter(user => user.isActive).length,
        totalRestaurants: restaurants.length,
        pendingOrders: orders.filter(order => order.status === 'Pending').length,
        todayOrders: todayOrders.length
      });

      setRecentOrders(orders.slice(0, 5));
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const updateOrderStatus = async (orderId, status) => {
    try {
      await orderAPI.updateStatus(orderId, status);
      fetchDashboardData();
    } catch (error) {
      console.error('Error updating order:', error);
    }
  };

  if (loading) {
    return <div style={styles.loading}>Loading Dashboard...</div>;
  }

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <div>
          <h1 style={styles.title}>Admin Dashboard</h1>
          <p style={styles.subtitle}>Real-time Food Delivery Management</p>
        </div>
        <div style={styles.timeContainer}>
          <div style={styles.liveIndicator}>
            <span style={styles.liveDot}></span>
            LIVE
          </div>
          <div style={styles.currentTime}>
            {currentTime.toLocaleTimeString()}
          </div>
        </div>
      </div>

      {/* Stats Cards */}
      <div style={styles.statsGrid}>
        <div style={{...styles.statCard, ...styles.primaryCard}}>
          <div style={styles.statIcon}>üìä</div>
          <div>
            <h3 style={styles.statNumber}>{stats.totalOrders}</h3>
            <p style={styles.statLabel}>Total Orders</p>
          </div>
        </div>

        <div style={{...styles.statCard, ...styles.successCard}}>
          <div style={styles.statIcon}>üí∞</div>
          <div>
            <h3 style={styles.statNumber}>${stats.totalRevenue.toFixed(2)}</h3>
            <p style={styles.statLabel}>Total Revenue</p>
          </div>
        </div>

        <div style={{...styles.statCard, ...styles.warningCard}}>
          <div style={styles.statIcon}>‚è≥</div>
          <div>
            <h3 style={styles.statNumber}>{stats.pendingOrders}</h3>
            <p style={styles.statLabel}>Pending Orders</p>
          </div>
        </div>

        <div style={{...styles.statCard, ...styles.infoCard}}>
          <div style={styles.statIcon}>üë•</div>
          <div>
            <h3 style={styles.statNumber}>{stats.activeUsers}</h3>
            <p style={styles.statLabel}>Active Users</p>
          </div>
        </div>

        <div style={{...styles.statCard, ...styles.dangerCard}}>
          <div style={styles.statIcon}>üè™</div>
          <div>
            <h3 style={styles.statNumber}>{stats.totalRestaurants}</h3>
            <p style={styles.statLabel}>Restaurants</p>
          </div>
        </div>

        <div style={{...styles.statCard, ...styles.darkCard}}>
          <div style={styles.statIcon}>üìÖ</div>
          <div>
            <h3 style={styles.statNumber}>{stats.todayOrders}</h3>
            <p style={styles.statLabel}>Today's Orders</p>
          </div>
        </div>
      </div>

      {/* Recent Orders */}
      <div style={styles.section}>
        <div style={styles.sectionHeader}>
          <h2 style={styles.sectionTitle}>Recent Orders</h2>
          <div style={styles.refreshBtn} onClick={fetchDashboardData}>
            üîÑ Refresh
          </div>
        </div>

        <div style={styles.ordersContainer}>
          {recentOrders.length === 0 ? (
            <div style={styles.noOrders}>No recent orders</div>
          ) : (
            recentOrders.map(order => (
              <div key={order._id} style={styles.orderCard}>
                <div style={styles.orderHeader}>
                  <div>
                    <h4 style={styles.orderRestaurant}>
                      {order.restaurant?.name || 'Unknown Restaurant'}
                    </h4>
                    <p style={styles.orderCustomer}>
                      {order.user?.name || 'Unknown Customer'}
                    </p>
                  </div>
                  <div style={styles.orderAmount}>
                    ${order.totalAmount?.toFixed(2) || '0.00'}
                  </div>
                </div>

                <div style={styles.orderStatus}>
                  <select 
                    value={order.status || 'Pending'}
                    onChange={(e) => updateOrderStatus(order._id, e.target.value)}
                    style={{
                      ...styles.statusSelect,
                      backgroundColor: getStatusColor(order.status)
                    }}
                  >
                    <option value="Pending">Pending</option>
                    <option value="Preparing">Preparing</option>
                    <option value="Delivered">Delivered</option>
                  </select>
                  <span style={styles.orderTime}>
                    {new Date(order.createdAt).toLocaleTimeString()}
                  </span>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Quick Actions */}
      <div style={styles.quickActions}>
        <h3 style={styles.quickTitle}>Quick Actions</h3>
        <div style={styles.actionButtons}>
          <button style={{...styles.actionBtn, backgroundColor: '#ff6b35'}}>
            üìã View All Orders
          </button>
          <button style={{...styles.actionBtn, backgroundColor: '#28a745'}}>
            üè™ Manage Restaurants
          </button>
          <button style={{...styles.actionBtn, backgroundColor: '#17a2b8'}}>
            üë• User Management
          </button>
          <button style={{...styles.actionBtn, backgroundColor: '#6f42c1'}}>
            üìä Analytics
          </button>
        </div>
      </div>
    </div>
  );
};

const getStatusColor = (status) => {
  switch(status) {
    case 'Pending': return '#ffc107';
    case 'Preparing': return '#17a2b8';
    case 'Delivered': return '#28a745';
    default: return '#6c757d';
  }
};

const styles = {
  container: {
    padding: '2rem',
    backgroundColor: '#f8f9fa',
    minHeight: '100vh'
  },
  header: {
    display: 'flex',
