    transition: 'transform 0.3s',
    cursor: 'pointer'
  },
  primaryCard: { borderLeft: '5px solid #007bff' },
  successCard: { borderLeft: '5px solid #28a745' },
  warningCard: { borderLeft: '5px solid #ffc107' },
  infoCard: { borderLeft: '5px solid #17a2b8' },
  dangerCard: { borderLeft: '5px solid #dc3545' },
  darkCard: { borderLeft: '5px solid #6c757d' },
